import './App.css';
import Header from './Header';
import ContactList from './ContactList';
import { useEffect, useState } from 'react';
import ContactModal from './ContactModal';



function App() {

	// state to fetch data
	const [userData, setUserData] = useState([])

	useEffect(() => {

		fetch('https://jsonplaceholder.typicode.com/users')
  			.then(response => response.json())
  			.then(json => setUserData(json))
	}, [])

	// Pass state into the component, like how you are passing {contacts} in contactList
	// Replace {contacts} with your api data instead of the static data you typed up
	return (
		<div className="uiContainer">
			<div>
				<Header />
				<ContactList contacts={userData}/>
			</div>
		</div>
	);

};

// How i would do the rest of this 

// 1. Currently you render a hard coded array with Cherry and Stevo. Fetch the data and render this instead.
// 2. When you click on a contact card, make a blank modal show. 
// 3. Add a close button to the modal, when you click on it use the setModalVisible function to hide it by setting the state to false
// 4. When you click on a contact card, pass the user data of that specific card to the modal
// 5. Render the user data on the modal
// 6. Fix responsiveness
// 7. Make it look pretty, add hover effects, find a better profile image
// 8. Bonus points - add a button that lets you email the contact, make sure everyones website link is a hyperlink <a></a> 
// 9. Change the title of the project in index.html -> <title>React App</title>

export default App;
