import React from 'react';
import user_icon from '../images/user_icon.svg';
import './ContactModal.css';

// You can also do this instead of just calling it 'props' and having to extract each thing
const ContactModal = ({data, setModalVisible}) => {
    // When a card is clicked, show all this information, edit the css :)
    const {name, username, email, phone, website} = data;

    // Set Modal visible to FALSE, if the user clicks t2he close button or outside the modal
    return (
        <div className="popup">
            <div className="modal-content">
                <div className="close" onClick={() => {setModalVisible(false)}}>&times;</div>
                <div className="header-box">
                    <img className="avatar" src={user_icon} alt="user_icon"/>
                    <div className="name">{name}</div>
                    <div className="username">{username}</div>
                </div>
                <div className="email">{email}</div>
                <div className="phone">{phone}</div>
                <div className="website">{website}</div>
            </div>

        </div>
        
    );
}

export default ContactModal;
