import React, {useState} from 'react';
import ContactCard from './ContactCard';
import './ContactList.css';
import ContactModal from './ContactModal'

const ContactList = ({contacts}) => {

    console.log("Hello")
    // When a contact card is clicked, we will show a modal that shows a detailed version of that user
    const [user, setUser] = useState({})

    // This state determines wheter the modal is visible or not. It is set to false initially since the modal only shows 
    // when a contact card is clicked
    const [modalVisible, setModalVisible] = useState(false)

    // This function handles when a contact card is clicked
    function showDetailedUser(user) {
        setUser(user)
        setModalVisible(true);
    }

    // Bad practice to use stuff directly from props like props.contact.map,
    // Look at how i deal with props in ContactModal
    // Instead of ContactList = (props) => {, do ContactList = ({contact}) => {
    // then here you can just do const renderContactList = contacts.map 
    const renderContactList = contacts.map;

    return <>
    <div className="allContacts">{renderContactList}
    
    </div>

    {modalVisible && <ContactModal data={user} visible={modalVisible} setModalVisible={setModalVisible} ></ContactModal>}
    
    </>;
};

export default ContactList;
